mapper.select:
select t.uuid, t.name, t.type, t.state, t.begin_date As beginDate, t.end_date As endDate, t.start_time As startTime, t.end_time AS endTime, t.run_week As runWeek, t.analy_start_time AS analyStartTime , t.analy_end_time AS analyEndTime , t.analy_type AS analyType, t.auto_start AS autoStart, t.is_deleted AS isDeleted, su.username As createUser, t.create_user As createUserUuid, t.create_time AS createTime, t.modify_user AS modifyUser, t.modify_time AS modifyTime, t.analy_begin_date AS analyBeginDate, t.analy_end_date AS analyEndDate, t.video_speed AS videoSpeed from task t left join sys_user su on t.create_user = su.uuid
1=1
and t.name like CONCAT('%',#{f.name},'%')
and t.type = #{f.type}
and t.state = #{f.state}
and t.is_deleted = 0
order by t.create_time desc
LIMIT #{beginCount},#{pageRows}

select count(t.uuid) from task t left join sys_user su on t.create_user = su.uuid
1=1
and t.name like CONCAT('%',#{f.name},'%')
and t.type = #{f.type}
and t.state = #{f.state}
and t.is_deleted = 0

SET SESSION group_concat_max_len=18446744073709551615; SELECT * FROM ( SELECT t.uuid taskUuid, t.name taskName, (SELECT GROUP_CONCAT(c.channel_name order by c.uuid ) FROM channel c, task_channel tc WHERE tc.channel_uuid = c.uuid AND tc.task_uuid = t.uuid) channelNames, t.analy_type, t.begin_date, t.end_date, t.start_time, t.end_time, t.type, t.run_week As runWeek, su.uuid createUserUuid, su.username createUser, t.state, t.analy_start_time, t.analy_end_time, t.create_time, t.is_deleted As isDel FROM task t LEFT JOIN sys_user su ON t.create_user = su.uuid WHERE 1=1
AND t.type = #{f.type}
and t.is_deleted = 0
AND t.state=#{f.state}
) tab
WHERE LOCATE( CONCAT( #{f.name}),tab.channelNames)>0 or LOCATE( CONCAT( #{f.name}),tab.taskName)>0
order by tab.create_time desc
LIMIT #{beginCount},#{pageRows}

SELECT COUNT(1) FROM ( SELECT t.uuid taskUuid, t.name taskName, (SELECT GROUP_CONCAT(c.channel_name SEPARATOR '、') FROM channel c, task_channel tc WHERE tc.channel_uuid = c.uuid AND tc.task_uuid = t.uuid) channelNames, (SELECT COUNT(1) FROM task_channel tc WHERE tc.task_uuid = t.uuid) channelCount, t.analy_type, t.begin_date, t.end_date, t.start_time, t.end_time, su.uuid createUserUuid, su.username createUser, t.state, t.analy_start_time, t.analy_end_time FROM task t LEFT JOIN sys_user su ON t.create_user = su.uuid WHERE t.type = 1 and t.is_deleted = 0
AND t.state=#{f.state}
) tab
WHERE LOCATE( CONCAT( #{f.name}),tab.channelNames)>0 or LOCATE( CONCAT( #{f.name}),tab.taskName)>0

